mapper.select:
select
t.id as id,
t.name,
t.description,
t.image,
t.job_params as jobParams,
t.params,
t.version,
t.insert_datetime as insertDatetime,
t.update_datetime as updateDatetime,
t.valid as valid
from
t_bot_info as t

    valid = 1

        and t.name = #{name}


        and t.description = #{description}


        and t.image = #{image}


        and t.job_params = #{jobParams}


        and t.version = #{version}


        AND JSON_CONTAINS(
        t.params,

            #{key}, #{value}

        )
